/* Hide annoying stuff */
.give-gold-button,
.goldvertisement,
.embed-comment,
.nub {
	display: none !important;
}
/* Reset */
*, *:before, *:after {
	background-color: transparent !important;
	border: none !important;
	color: #777 !important;
}
/* Default links */
a {
	color: #48b !important;
}
a:visited {
	color: #369 !important;
}
a:hover {
	color: #5ad !important;
}
/* Background */
html,
body,
#sr-more-link {
	background-color: #222 !important;
}
/* Top bar */
#header-img,
#mail,
.sidebox.create .spacer a {
	filter: invert(80%) hue-rotate(180deg) !important;
}
.drop-choices,
.hover-bubble,
.modal-dialog {
	background: #111 !important;
}
.tabmenu .selected a {
	color: #999 !important;
}
