    display: none !important;
}
span.mark{ /*API page text backgrounds*/
    background: #222 !important;
    border: 1px solid #333 !important;
}
.lang_div{ /*API page horizontal lines or separators*/
    border-bottom: 1px solid #333 !important;
}

/*Style settings below. They should remain as commented double brackets.*/
.banner_728, #steadfast, div#content_right div div iframe, html body div[id^='VDX'], html body div[id^='tf_'], div#content_left div[style="padding: 0;"], div#content_right div[style="padding: 0;"], html body div[id^='TFIBV'], .fancybar_show ._fancybar, div[id^='topdiv'], div[style*="z-index: 1005000;"], div[id^='prw'], div[id^='ftdiv'], iframe[id^='tfa'], div[id^='teaser'], div[id^='paypal'], div[id^='tagcade'], div[style='padding: 10px 0 0 0;'] { /* Ad Removal */
    display: none !important;
}
div#abrpm3, iframe#monkey{ /* PRO Ad Removal */
    display: none !important;
}

