/* Hide some things I dislike seeing */

/* Very simple ad hider */
[class="ad"],
[class *="ads"],
[class ^="ad-"],
[class ^="ad_"],
[class *="-ad-"],
[class $="-ad"],
[class $="_ad"],
[id *="_ad"]{
    display: none;
}
